mapper.select:
WITH REPLYCNT AS ( SELECT BO_NO, COUNT(REP_NO) REP_CNT FROM REPLY GROUP BY BO_NO ) SELECT B.* FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT A.BO_NO , BO_CONTENT, BO_TITLE , BO_WRITER, TO_CHAR(BO_DATE, 'YYYY-MM-DD HH:MI') BO_DATE , BO_HIT, BO_REC, BO_IP , BO_MAIL , BO_PARENT , NVL(REP_CNT, 0) REP_CNT FROM BOARD A LEFT OUTER JOIN REPLYCNT B ON (A.BO_NO = B.BO_NO)
ORDER BY A.BO_NO DESC ) A ) B
WHERE RNUM BETWEEN #{startRow} AND #{endRow}
